import React, { useState, useEffect } from 'react';
import './css/App.css';
import Forms from './pages/forms.js';
import Sale from './pages/sale.js';
import logo from './img/logo.png';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '@fortawesome/fontawesome-free/css/all.css'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

AOS.init({duration: 500});


function App() {
  const [topProduct, setTopProduct] = useState('');
  const [topEmployee, setTopEmployee] = useState('');
  const [products, setProducts] = useState('');

  function findTopProduct(){  
    if(localStorage.getItem('Sales') === null){
      return(
        "There are no sales"
      )
    }else{
      let sales = JSON.parse(localStorage.getItem('Sales'));

      //Each index in this array holds the same index as the products array and tracks the total sold ammount
      let helper = []
      for(let k = 0; k < products.length; k++){
        helper.push(0);
      }

      for(let i = 0; i < sales.length; i++){
        for(let j = 0; j < products.length; j++){
          console.log(sales[i], products[j]);
          helper[j] += parseInt(sales[i][products[j].replace(/ +/g, "")]);
        }
      }

      console.log(helper);




      }
    }

